CREATE FUNCTION public.analyze_search_performance(search_term text) RETURNS TABLE(search_type text, result_count bigint, execution_time numeric, average_rank numeric, index_used text)
    LANGUAGE plpgsql
    AS $_$
DECLARE
    start_time timestamp;
    end_time timestamp;
    index_info text;
BEGIN
    -- Basic search
    start_time := clock_timestamp();
    RETURN QUERY EXECUTE '
        SELECT 
            ''basic'' as search_type,
            count(*) as result_count,
            extract(epoch from (clock_timestamp() - $1)) * 1000 as execution_time,
            avg(ts_rank(tsv, plainto_tsquery($2))) as average_rank,
            (SELECT indexname FROM pg_indexes WHERE tablename = ''search_index'' AND indexdef LIKE ''%tsv%'' LIMIT 1) as index_used
        FROM search_index 
        WHERE tsv @@ plainto_tsquery($2)'
    USING start_time, search_term;
    
    -- Phrase search
    start_time := clock_timestamp();
    RETURN QUERY EXECUTE '
        SELECT 
            ''phrase'' as search_type,
            count(*) as result_count,
            extract(epoch from (clock_timestamp() - $1)) * 1000 as execution_time,
            avg(ts_rank(tsv, phraseto_tsquery($2))) as average_rank,
            (SELECT indexname FROM pg_indexes WHERE tablename = ''search_index'' AND indexdef LIKE ''%tsv%'' LIMIT 1) as index_used
        FROM search_index 
