--

CREATE FUNCTION public.calculate_course_progress(course_id_param uuid, user_id_param uuid) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
  total_lessons INTEGER;
  completed_lessons INTEGER;
  progress_percent INTEGER;
BEGIN
  -- Get total lessons in course
  SELECT COUNT(cl.id) INTO total_lessons
  FROM course_lessons cl
  JOIN course_modules cm ON cm.id = cl.module_id
  WHERE cm.course_id = course_id_param;
  
  -- Get completed lessons by user
  SELECT COUNT(ulp.id) INTO completed_lessons
  FROM user_lesson_progress ulp
  JOIN course_lessons cl ON cl.id = ulp.lesson_id
  JOIN course_modules cm ON cm.id = cl.module_id
  WHERE cm.course_id = course_id_param 
    AND ulp.user_id = user_id_param 
    AND ulp.completed = true;
  
  -- Calculate percentage
  IF total_lessons > 0 THEN
    progress_percent := ROUND((completed_lessons::NUMERIC / total_lessons::NUMERIC) * 100);
  ELSE
    progress_percent := 0;
  END IF;
  
  -- Update user course progress
  INSERT INTO user_course_progress (user_id, course_id, progress, last_accessed)
  VALUES (user_id_param, course_id_param, progress_percent, now())
  ON CONFLICT (user_id, course_id) 
  DO UPDATE SET 
    progress = EXCLUDED.progress,
    last_accessed = EXCLUDED.last_accessed,
    completed = (EXCLUDED.progress = 100),
    completed_at = CASE WHEN EXCLUDED.progress = 100 AND user_course_progress.completed = false THEN now() ELSE user_course_progress.completed_at END;
  
  RETURN progress_percent;
END;
$$;


