--
-- Name: calculate_progress(jsonb, jsonb); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.calculate_progress(calculator jsonb, requirement jsonb) RETURNS integer
    LANGUAGE plpgsql
    SET search_path TO ''
    AS $$
DECLARE
  req_type TEXT;
  req_value NUMERIC;
  user_value NUMERIC;
  progress INTEGER;
BEGIN
  req_type := requirement->>'type';
  req_value := (requirement->>'value')::NUMERIC;
  
  -- Get user value based on requirement type
  user_value := CASE req_type
    WHEN 'lessons_completed' THEN (calculator->>'completed_lessons')::NUMERIC
    WHEN 'practice_streak' THEN (calculator->>'practice_streak')::NUMERIC
    WHEN 'total_practice_minutes' THEN (calculator->>'total_practice_minutes')::NUMERIC
    WHEN 'community_interactions' THEN (calculator->>'community_interactions')::NUMERIC
    WHEN 'courses_completed' THEN (calculator->>'completed_courses')::NUMERIC
    WHEN 'chords_learned' THEN (calculator->>'chords_learned')::NUMERIC
    WHEN 'songs_learned' THEN (calculator->>'songs_learned')::NUMERIC
    WHEN 'video_lessons_watched' THEN (calculator->>'videos_watched')::NUMERIC
    WHEN 'tutor_sessions' THEN (calculator->>'tutor_sessions')::NUMERIC
    WHEN 'profile_completion' THEN (calculator->>'profile_completion')::NUMERIC
    WHEN 'referrals_made' THEN (calculator->>'referrals')::NUMERIC
    WHEN 'tools_used' THEN (calculator->>'tools_used_count')::NUMERIC
    WHEN 'help_given' THEN (calculator->>'help_given')::NUMERIC
    WHEN 'christmas_songs' THEN (calculator->>'christmas_songs_learned')::NUMERIC
    WHEN 'piano_course_completed' THEN CASE WHEN (calculator->>'piano_course_completed')::BOOLEAN THEN 1 ELSE 0 END
    WHEN 'guitar_course_completed' THEN CASE WHEN (calculator->>'guitar_course_completed')::BOOLEAN THEN 1 ELSE 0 END
    WHEN 'vocal_course_completed' THEN CASE WHEN (calculator->>'vocal_course_completed')::BOOLEAN THEN 1 ELSE 0 END
    WHEN 'worship_course_completed' THEN CASE WHEN (calculator->>'worship_course_completed')::BOOLEAN THEN 1 ELSE 0 END
    WHEN 'achievements_unlocked' THEN (calculator->>'achievements_unlocked')::NUMERIC
    ELSE 0
  END;
  
  -- Calculate progress percentage
  progress := LEAST(100, GREATEST(0, (user_value / NULLIF(req_value, 0)) * 100));
  
  RETURN progress;
END;
$$;


