--

CREATE FUNCTION public.calculate_user_streak(p_user_id uuid) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    current_streak INTEGER := 0;
    last_date DATE;
    check_date DATE;
BEGIN
    SELECT MAX(session_date) INTO last_date
    FROM user_study_sessions
    WHERE user_id = p_user_id;
    
    IF last_date IS NULL THEN
        RETURN 0;
    END IF;
    
    -- Check if streak is broken (no activity yesterday or today)
    IF last_date < CURRENT_DATE - INTERVAL '1 day' THEN
        RETURN 0;
    END IF;
    
    check_date := last_date;
    
    WHILE EXISTS (
        SELECT 1 FROM user_study_sessions
        WHERE user_id = p_user_id AND session_date = check_date
    ) LOOP
        current_streak := current_streak + 1;
        check_date := check_date - INTERVAL '1 day';
    END LOOP;
    
    RETURN current_streak;
END;
$$;


