CREATE FUNCTION public.can_access_resource(resource_id uuid) RETURNS boolean
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  resource_access_level TEXT;
  user_tier TEXT;
  user_role TEXT;
BEGIN
  -- Get the resource's access level
  SELECT access_level INTO resource_access_level
  FROM public.resources
  WHERE id = resource_id;
  
  -- If resource doesn't exist, return false
  IF resource_access_level IS NULL THEN
    RETURN FALSE;
  END IF;
  
  -- Free resources are accessible to everyone
  IF resource_access_level = 'free' THEN
    RETURN TRUE;
  END IF;
  
  -- For other access levels, user must be authenticated
  IF auth.uid() IS NULL THEN
    RETURN FALSE;
  END IF;
  
  -- Get user's subscription tier and role
  SELECT subscription_tier, role INTO user_tier, user_role
  FROM public.profiles
  WHERE id = auth.uid();
  
  -- Admins and tutors can access everything
  IF user_role IN ('admin', 'tutor') THEN
    RETURN TRUE;
  END IF;
  
  -- Check access based on subscription tier
  CASE resource_access_level
    WHEN 'auth' THEN
      RETURN TRUE; -- Any authenticated user
    WHEN 'basic' THEN
      RETURN user_tier IN ('basic', 'premium', 'professional');
    WHEN 'premium' THEN
      RETURN user_tier IN ('premium', 'professional');
    WHEN 'professional' THEN
      RETURN user_tier = 'professional';
    ELSE
      RETURN FALSE;
  END CASE;
END;
$$;


