    WHEN 'achievements_unlocked' THEN (calculator->>'achievements_unlocked')::NUMERIC
    ELSE 0
  END;
  
  -- Check requirement based on operator
  RETURN CASE req_operator
    WHEN '>=' THEN user_value >= req_value
    WHEN '>' THEN user_value > req_value
    WHEN '=' THEN user_value = req_value
    WHEN '<=' THEN user_value <= req_value
