CREATE FUNCTION public.handle_new_user() RETURNS trigger
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
    user_role_text text;
    user_role_enum app_role;
BEGIN
    -- Determine role from metadata or default
    user_role_text := COALESCE(NEW.raw_user_meta_data ->> 'role', 'user');
    
    -- Map to enum (safely)
    user_role_enum := CASE 
        WHEN user_role_text = 'admin' THEN 'admin'::app_role
        WHEN user_role_text = 'tutor' THEN 'tutor'::app_role
        ELSE 'user'::app_role
    END;
    
    -- Insert/update profiles
    INSERT INTO public.profiles (id, email, role, subscription_tier)
    VALUES (
        NEW.id, 
        NEW.email,
        user_role_text,
        'free'
    )
    ON CONFLICT (id) DO UPDATE SET
        email = EXCLUDED.email,
        role = EXCLUDED.role;
    
    -- Insert into user_roles (trigger will handle consistency)
    INSERT INTO public.user_roles (user_id, role)
    VALUES (NEW.id, user_role_enum)
    ON CONFLICT (user_id, role) DO NOTHING;
    
    RETURN NEW;
END;
$$;


