    LANGUAGE plpgsql
    AS $$
DECLARE
  event_data JSON;
  event_type TEXT;
BEGIN
  IF TG_OP = 'INSERT' THEN
    event_type := 'INSERT';
    event_data := json_build_object(
      'event', event_type,
      'data', row_to_json(NEW)
    );
  ELSIF TG_OP = 'UPDATE' THEN
    event_type := 'UPDATE';
    event_data := json_build_object(
      'event', event_type,
      'data', row_to_json(NEW),
      'old', row_to_json(OLD)
    );
  ELSIF TG_OP = 'DELETE' THEN
    event_type := 'DELETE';
    event_data := json_build_object(
      'event', event_type,
      'data', row_to_json(OLD)
    );
  END IF;
  
  PERFORM pg_notify('resource_changes', event_data::text);
  
  RETURN NEW;
END;
$$;


