CREATE FUNCTION public.search_all(_q text, _limit integer DEFAULT 20, _offset integer DEFAULT 0) RETURNS TABLE(source_table text, source_id uuid, title text, snippet text, rank double precision, metadata jsonb)
    LANGUAGE sql
    AS $$
  WITH q AS (
    SELECT websearch_to_tsquery('simple', _q) AS qry
  ),
  matched AS (
    SELECT
      si.source_table,
      si.source_id,
      coalesce(si.title, '') as title,
      ts_rank_cd(si.tsv, q.qry) AS rank,
      si.metadata,
      ts_headline('simple', si.body, q.qry, 'StartSel=<b>,StopSel=</b>,MaxFragments=2,MaxWords=20') AS snippet
    FROM public.search_index si, q
    WHERE si.tsv @@ q.qry
  )
  SELECT
    source_table,
    source_id,
    title,
    coalesce(snippet, '') AS snippet,
    rank,
    metadata
  FROM matched
  ORDER BY rank DESC
  LIMIT _limit
  OFFSET _offset;
$$;


