-- Name: update_user_achievements(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.update_user_achievements() RETURNS trigger
    LANGUAGE plpgsql
    SET search_path TO ''
    AS $$
DECLARE
  achievement_record RECORD;
  calculator JSONB;
  progress_val INTEGER;
  unlocked_val BOOLEAN;
  requirements_met BOOLEAN;
  requirement RECORD;
BEGIN
  -- For each achievement, check if user meets requirements
  FOR achievement_record IN SELECT * FROM achievements LOOP
    -- Initialize calculator with user data
    calculator := jsonb_build_object(
      'completed_lessons', COALESCE(NEW.completed_lessons, 0),
      'practice_streak', COALESCE(NEW.practice_streak, 0),
      'total_practice_minutes', COALESCE(NEW.total_practice_minutes, 0),
      'community_interactions', COALESCE(NEW.community_interactions, 0),
      'completed_courses', COALESCE(NEW.completed_courses, 0),
      'chords_learned', COALESCE(NEW.chords_learned, 0),
      'songs_learned', COALESCE(NEW.songs_learned, 0),
      'videos_watched', COALESCE(NEW.videos_watched, 0),
      'tutor_sessions', COALESCE(NEW.tutor_sessions, 0),
      'profile_completion', COALESCE(NEW.profile_completion, 0),
      'referrals', COALESCE(NEW.referrals, 0),
      'tools_used_count', COALESCE(NEW.tools_used_count, 0),
      'help_given', COALESCE(NEW.help_given, 0),
      'christmas_songs_learned', COALESCE(NEW.christmas_songs_learned, 0),
      'piano_course_completed', COALESCE(NEW.piano_course_completed, FALSE),
      'guitar_course_completed', COALESCE(NEW.guitar_course_completed, FALSE),
      'vocal_course_completed', COALESCE(NEW.vocal_course_completed, FALSE),
      'worship_course_completed', COALESCE(NEW.worship_course_completed, FALSE)
    );
    
    -- Check all requirements
    requirements_met := TRUE;
    FOR requirement IN SELECT * FROM jsonb_array_elements(achievement_record.requirements) LOOP
      IF NOT check_requirement(calculator, requirement) THEN
        requirements_met := FALSE;
        EXIT;
      END IF;
    END LOOP;
    
    -- Calculate progress based on the first requirement
    progress_val := calculate_progress(calculator, achievement_record.requirements->0);
    unlocked_val := requirements_met;
    
    -- Insert or update user achievement
    INSERT INTO user_achievements (user_id, achievement_id, progress, unlocked, unlocked_at)
    VALUES (NEW.user_id, achievement_record.id, progress_val, unlocked_val, 
            CASE WHEN unlocked_val AND NOT EXISTS (
              SELECT 1 FROM user_achievements 
              WHERE user_id = NEW.user_id AND achievement_id = achievement_record.id AND unlocked = TRUE
            ) THEN NOW() ELSE NULL END)
    ON CONFLICT (user_id, achievement_id) 
    DO UPDATE SET 
      progress = EXCLUDED.progress,
      unlocked = EXCLUDED.unlocked,
      unlocked_at = CASE 
        WHEN EXCLUDED.unlocked AND user_achievements.unlocked = FALSE THEN NOW()
        ELSE user_achievements.unlocked_at 
      END,
      updated_at = NOW();
  END LOOP;
  
  -- Update achievements_unlocked count
  NEW.achievements_unlocked := (
    SELECT COUNT(*) 
    FROM user_achievements 
    WHERE user_id = NEW.user_id AND unlocked = TRUE
  );
  
  RETURN NEW;
END;
$$;


