--
-- Name: validate_password_security(text); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.validate_password_security(password text) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO ''
    AS $_$
DECLARE
    result jsonb := '{}';
    score integer := 0;
    issues text[] := ARRAY[]::text[];
    password_hash text;
    is_weak boolean := false;
BEGIN
    -- Basic validation
    IF password IS NULL OR length(password) = 0 THEN
        RETURN jsonb_build_object(
            'valid', false,
            'score', 0,
            'issues', ARRAY['Password cannot be empty']
        );
    END IF;
    
    -- Check minimum length (8 characters)
    IF length(password) < 8 THEN
        issues := array_append(issues, 'Password must be at least 8 characters long');
    ELSE
        score := score + 1;
    END IF;
    
    -- Check maximum length (prevent DoS)
    IF length(password) > 128 THEN
        issues := array_append(issues, 'Password cannot exceed 128 characters');
    END IF;
    
    -- Check for lowercase letters
    IF password !~ '[a-z]' THEN
        issues := array_append(issues, 'Password must contain lowercase letters');
    ELSE
        score := score + 1;
    END IF;
    
    -- Check for uppercase letters
    IF password !~ '[A-Z]' THEN
        issues := array_append(issues, 'Password must contain uppercase letters');
    ELSE
        score := score + 1;
    END IF;
    
    -- Check for numbers
    IF password !~ '[0-9]' THEN
        issues := array_append(issues, 'Password must contain numbers');
    ELSE
        score := score + 1;
    END IF;
    
    -- Check for special characters
    IF password !~ '[^a-zA-Z0-9]' THEN
        issues := array_append(issues, 'Password must contain special characters (!@#$%^&*()_+-=[]{}|;:,.<>?)');
    ELSE
        score := score + 1;
    END IF;
    
    -- Check against common weak passwords
    password_hash := encode(digest(lower(password), 'sha256'), 'hex');
    SELECT EXISTS(
        SELECT 1 FROM public.weak_passwords 
        WHERE password_hash = validate_password_security.password_hash
    ) INTO is_weak;
    
    IF is_weak THEN
        issues := array_append(issues, 'This password is commonly used and easily guessed');
        score := score - 2;
    END IF;
    
    -- Check for keyboard patterns
    IF password ~* '(qwert|asdf|zxcv|1234|abcd)' THEN
        issues := array_append(issues, 'Password contains keyboard patterns');
        score := score - 1;
    END IF;
    
    -- Check for repeated characters
    IF password ~ '(.)\1{2,}' THEN
        issues := array_append(issues, 'Password contains too many repeated characters');
        score := score - 1;
    END IF;
    
    -- Check for common substitutions
    IF password ~* '(p@ssw0rd|passw0rd|p4ssword|admin|login|welcome)' THEN
        issues := array_append(issues, 'Password uses common word substitutions');
        score := score - 1;
    END IF;
    
    -- Ensure score is not negative
    score := GREATEST(score, 0);
    
    result := jsonb_build_object(
        'valid', score >= 4 AND array_length(issues, 1) IS NULL,
        'score', score,
        'max_score', 5,
        'strength', CASE 
            WHEN score >= 5 THEN 'Very Strong'
            WHEN score >= 4 THEN 'Strong'
            WHEN score >= 3 THEN 'Medium'
            WHEN score >= 2 THEN 'Weak'
            ELSE 'Very Weak'
        END,
        'issues', to_jsonb(issues),
        'suggestions', to_jsonb(ARRAY[
            'Use a mix of uppercase and lowercase letters',
            'Include numbers and special characters',
            'Avoid common words and patterns',
            'Make it at least 8 characters long',
            'Avoid personal information'
        ])
    );
    
    RETURN result;
END;
$_$;


ALTER FUNCTION public.validate_password_security(password text) OWNER TO postgres;

--
-- Name: verify_admin_status(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.verify_admin_status() RETURNS TABLE(has_admin_access boolean, is_admin boolean, is_authenticated boolean, user_id uuid, email text, role text, display_name text, subscription_tier text, message text)
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
  _user_id UUID;
  _email TEXT;
  _role TEXT;
  _is_admin BOOLEAN;
  _display_name TEXT;
  _subscription_tier TEXT;
BEGIN
  -- Get current user ID
  _user_id := auth.uid();
  
  IF _user_id IS NULL THEN
    RETURN QUERY SELECT 
      FALSE as has_admin_access,
      FALSE as is_admin,
      FALSE as is_authenticated,
      NULL::UUID as user_id,
      NULL::TEXT as email,
      NULL::TEXT as role,
      NULL::TEXT as display_name,
      NULL::TEXT as subscription_tier,
      'No authenticated user found' as message;
    RETURN;
  END IF;
  
  -- Get user info
  SELECT auth.email() INTO _email;
  
  -- Get profile info including role and admin status
  SELECT 
    p.role,
    COALESCE(p.is_admin, FALSE),
    p.display_name,
    p.subscription_tier
  INTO _role, _is_admin, _display_name, _subscription_tier
  FROM profiles p
  WHERE p.id = _user_id;
  
  -- Check if user is admin (either role='admin' or is_admin=true)
  IF (_role = 'admin' OR _is_admin = TRUE) THEN
    RETURN QUERY SELECT 
      TRUE as has_admin_access,
      TRUE as is_admin,
      TRUE as is_authenticated,
      _user_id as user_id,
      _email as email,
      COALESCE(_role, 'user') as role,
      _display_name as display_name,
      COALESCE(_subscription_tier, 'free') as subscription_tier,
      'Admin access granted' as message;
  ELSE
    RETURN QUERY SELECT 
      FALSE as has_admin_access,
      FALSE as is_admin,
      TRUE as is_authenticated,
      _user_id as user_id,
      _email as email,
      COALESCE(_role, 'user') as role,
      _display_name as display_name,
      COALESCE(_subscription_tier, 'free') as subscription_tier,
      'User does not have admin privileges' as message;
  END IF;
END;
$$;


ALTER FUNCTION public.verify_admin_status() OWNER TO postgres;

--
-- Name: apply_rls(jsonb, integer); Type: FUNCTION; Schema: realtime; Owner: supabase_admin
--

CREATE FUNCTION realtime.apply_rls(wal jsonb, max_record_bytes integer DEFAULT (1024 * 1024)) RETURNS SETOF realtime.wal_rls
    LANGUAGE plpgsql
    AS $$
declare
-- Regclass of the table e.g. public.notes
entity_ regclass = (quote_ident(wal ->> 'schema') || '.' || quote_ident(wal ->> 'table'))::regclass;

-- I, U, D, T: insert, update ...
action realtime.action = (
    case wal ->> 'action'
        when 'I' then 'INSERT'
        when 'U' then 'UPDATE'
        when 'D' then 'DELETE'
        else 'ERROR'
    end
);

-- Is row level security enabled for the table
is_rls_enabled bool = relrowsecurity from pg_class where oid = entity_;

subscriptions realtime.subscription[] = array_agg(subs)
    from
        realtime.subscription subs
    where
        subs.entity = entity_;

-- Subscription vars
roles regrole[] = array_agg(distinct us.claims_role::text)
    from
        unnest(subscriptions) us;

working_role regrole;
claimed_role regrole;
claims jsonb;

subscription_id uuid;
subscription_has_access bool;
visible_to_subscription_ids uuid[] = '{}';

-- structured info for wal's columns
columns realtime.wal_column[];
-- previous identity values for update/delete
old_columns realtime.wal_column[];

error_record_exceeds_max_size boolean = octet_length(wal::text) > max_record_bytes;

-- Primary jsonb output for record
output jsonb;

begin
perform set_config('role', null, true);

columns =
    array_agg(
        (
            x->>'name',
            x->>'type',
            x->>'typeoid',
            realtime.cast(
                (x->'value') #>> '{}',
                coalesce(
                    (x->>'typeoid')::regtype, -- null when wal2json version <= 2.4
                    (x->>'type')::regtype
                )
            ),
            (pks ->> 'name') is not null,
            true
        )::realtime.wal_column
    )
    from
        jsonb_array_elements(wal -> 'columns') x
        left join jsonb_array_elements(wal -> 'pk') pks
            on (x ->> 'name') = (pks ->> 'name');

old_columns =
    array_agg(
        (
            x->>'name',
            x->>'type',
            x->>'typeoid',
            realtime.cast(
                (x->'value') #>> '{}',
                coalesce(
                    (x->>'typeoid')::regtype, -- null when wal2json version <= 2.4
                    (x->>'type')::regtype
                )
            ),
            (pks ->> 'name') is not null,
            true
        )::realtime.wal_column
    )
    from
        jsonb_array_elements(wal -> 'identity') x
        left join jsonb_array_elements(wal -> 'pk') pks
            on (x ->> 'name') = (pks ->> 'name');

for working_role in select * from unnest(roles) loop

    -- Update `is_selectable` for columns and old_columns
    columns =
        array_agg(
            (
                c.name,
                c.type_name,
                c.type_oid,
                c.value,
                c.is_pkey,
                pg_catalog.has_column_privilege(working_role, entity_, c.name, 'SELECT')
            )::realtime.wal_column
        )
        from
            unnest(columns) c;

    old_columns =
            array_agg(
                (
                    c.name,
                    c.type_name,
                    c.type_oid,
                    c.value,
                    c.is_pkey,
                    pg_catalog.has_column_privilege(working_role, entity_, c.name, 'SELECT')
                )::realtime.wal_column
            )
            from
                unnest(old_columns) c;

    if action <> 'DELETE' and count(1) = 0 from unnest(columns) c where c.is_pkey then
        return next (
            jsonb_build_object(
                'schema', wal ->> 'schema',
                'table', wal ->> 'table',
                'type', action
            ),
            is_rls_enabled,
            -- subscriptions is already filtered by entity
            (select array_agg(s.subscription_id) from unnest(subscriptions) as s where claims_role = working_role),
            array['Error 400: Bad Request, no primary key']
        )::realtime.wal_rls;

    -- The claims role does not have SELECT permission to the primary key of entity
    elsif action <> 'DELETE' and sum(c.is_selectable::int) <> count(1) from unnest(columns) c where c.is_pkey then
        return next (
            jsonb_build_object(
                'schema', wal ->> 'schema',
                'table', wal ->> 'table',
                'type', action
            ),
            is_rls_enabled,
            (select array_agg(s.subscription_id) from unnest(subscriptions) as s where claims_role = working_role),
            array['Error 401: Unauthorized']
        )::realtime.wal_rls;

    else
        output = jsonb_build_object(
            'schema', wal ->> 'schema',
            'table', wal ->> 'table',
            'type', action,
            'commit_timestamp', to_char(
                ((wal ->> 'timestamp')::timestamptz at time zone 'utc'),
                'YYYY-MM-DD"T"HH24:MI:SS.MS"Z"'
            ),
            'columns', (
                select
                    jsonb_agg(
                        jsonb_build_object(
                            'name', pa.attname,
                            'type', pt.typname
                        )
                        order by pa.attnum asc
                    )
                from
                    pg_attribute pa
                    join pg_type pt
                        on pa.atttypid = pt.oid
                where
                    attrelid = entity_
                    and attnum > 0
                    and pg_catalog.has_column_privilege(working_role, entity_, pa.attname, 'SELECT')
            )
        )
        -- Add "record" key for insert and update
        || case
            when action in ('INSERT', 'UPDATE') then
                jsonb_build_object(
                    'record',
                    (
                        select
                            jsonb_object_agg(
                                -- if unchanged toast, get column name and value from old record
                                coalesce((c).name, (oc).name),
                                case
                                    when (c).name is null then (oc).value
                                    else (c).value
                                end
                            )
                        from
                            unnest(columns) c
                            full outer join unnest(old_columns) oc
                                on (c).name = (oc).name
                        where
                            coalesce((c).is_selectable, (oc).is_selectable)
                            and ( not error_record_exceeds_max_size or (octet_length((c).value::text) <= 64))
                    )
                )
            else '{}'::jsonb
        end
        -- Add "old_record" key for update and delete
        || case
            when action = 'UPDATE' then
                jsonb_build_object(
                        'old_record',
                        (
                            select jsonb_object_agg((c).name, (c).value)
                            from unnest(old_columns) c
                            where
                                (c).is_selectable
                                and ( not error_record_exceeds_max_size or (octet_length((c).value::text) <= 64))
                        )
                    )
            when action = 'DELETE' then
                jsonb_build_object(
                    'old_record',
                    (
                        select jsonb_object_agg((c).name, (c).value)
                        from unnest(old_columns) c
                        where
                            (c).is_selectable
                            and ( not error_record_exceeds_max_size or (octet_length((c).value::text) <= 64))
                            and ( not is_rls_enabled or (c).is_pkey ) -- if RLS enabled, we can't secure deletes so filter to pkey
                    )
                )
            else '{}'::jsonb
        end;

        -- Create the prepared statement
        if is_rls_enabled and action <> 'DELETE' then
            if (select 1 from pg_prepared_statements where name = 'walrus_rls_stmt' limit 1) > 0 then
                deallocate walrus_rls_stmt;
            end if;
            execute realtime.build_prepared_statement_sql('walrus_rls_stmt', entity_, columns);
        end if;

        visible_to_subscription_ids = '{}';

        for subscription_id, claims in (
                select
                    subs.subscription_id,
                    subs.claims
                from
                    unnest(subscriptions) subs
                where
                    subs.entity = entity_
                    and subs.claims_role = working_role
                    and (
                        realtime.is_visible_through_filters(columns, subs.filters)
                        or (
                          action = 'DELETE'
                          and realtime.is_visible_through_filters(old_columns, subs.filters)
                        )
                    )
        ) loop

            if not is_rls_enabled or action = 'DELETE' then
                visible_to_subscription_ids = visible_to_subscription_ids || subscription_id;
            else
                -- Check if RLS allows the role to see the record
                perform
                    -- Trim leading and trailing quotes from working_role because set_config
                    -- doesn't recognize the role as valid if they are included
                    set_config('role', trim(both '"' from working_role::text), true),
                    set_config('request.jwt.claims', claims::text, true);

                execute 'execute walrus_rls_stmt' into subscription_has_access;

                if subscription_has_access then
                    visible_to_subscription_ids = visible_to_subscription_ids || subscription_id;
                end if;
            end if;
        end loop;

        perform set_config('role', null, true);

        return next (
            output,
            is_rls_enabled,
            visible_to_subscription_ids,
            case
                when error_record_exceeds_max_size then array['Error 413: Payload Too Large']
                else '{}'
            end
        )::realtime.wal_rls;

    end if;
end loop;

perform set_config('role', null, true);
end;
$$;


