--
-- Name: admin_user_analytics; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.admin_user_analytics AS
 SELECT ("substring"((u.id)::text, 1, 8) || '****'::text) AS anonymized_user_id,
    p.subscription_tier,
    p.role AS user_role,
    up.level AS user_level,
    up.total_xp,
    up.courses_enrolled,
    up.courses_completed,
    up.modules_completed,
    up.classes_completed,
    up.lessons_completed,
    up.total_study_time_minutes,
    up.current_streak_days,
    up.longest_streak_days,
    up.avg_daily_study_minutes,
    round((((((((((COALESCE(up.rhythm_mastery, 0) + COALESCE(up.melody_mastery, 0)) + COALESCE(up.harmony_mastery, 0)) + COALESCE(up.timbre_mastery, 0)) + COALESCE(up.texture_mastery, 0)) + COALESCE(up.form_mastery, 0)) + COALESCE(up.dynamics_mastery, 0)) + COALESCE(up.tempo_mastery, 0)))::numeric / 8.0), 2) AS avg_element_mastery,
    round(((((((COALESCE(up.quiz_attempts_total, 0))::numeric * 0.2) + ((COALESCE(up.lessons_completed, 0))::numeric * 0.3)) + ((COALESCE(up.current_streak_days, 0))::numeric * 0.5)) + ((COALESCE(ucs.posts_created, 0))::numeric * 0.1)) + ((COALESCE(ucs.reputation_points, 0))::numeric * 0.05)), 2) AS engagement_score,
    COALESCE(ucs.threads_created, 0) AS threads_created,
    COALESCE(ucs.posts_created, 0) AS posts_created,
    COALESCE(ucs.reputation_points, 0) AS community_reputation,
    up.subscription_revenue_generated,
    up.lifetime_value,
    date(up.created_at) AS joined_date,
    up.last_study_date,
        CASE
            WHEN (up.last_study_date >= (CURRENT_DATE - '7 days'::interval)) THEN 'active'::text
            WHEN (up.last_study_date >= (CURRENT_DATE - '30 days'::interval)) THEN 'occasional'::text
            ELSE 'inactive'::text
        END AS activity_status
   FROM (((auth.users u
     LEFT JOIN public.profiles p ON ((p.id = u.id)))
     LEFT JOIN public.user_progress up ON ((up.user_id = u.id)))
     LEFT JOIN public.user_community_stats ucs ON ((ucs.user_id = u.id)))
  WHERE (u.deleted_at IS NULL);


