--

CREATE VIEW public.ai_performance_dashboard AS
 SELECT ai_interactions.model_used AS model_name,
    date(ai_interactions.created_at) AS date,
    count(*) AS total_requests,
    avg(ai_interactions.processing_time_ms) AS avg_response_time,
    sum(
        CASE
            WHEN ai_interactions.was_helpful THEN 1
            ELSE 0
        END) AS helpful_responses,
    count(DISTINCT ai_interactions.user_id) AS unique_users,
    sum((ai_interactions.input_tokens + ai_interactions.output_tokens)) AS total_tokens_used
   FROM public.ai_interactions
  WHERE (ai_interactions.created_at >= (now() - '30 days'::interval))
  GROUP BY ai_interactions.model_used, (date(ai_interactions.created_at))
  ORDER BY (date(ai_interactions.created_at)) DESC, (count(*)) DESC;


