CREATE MATERIALIZED VIEW public.resources_by_category AS
 SELECT r.category_id,
    rc.name AS category_name,
    count(*) AS total_resources,
    count(*) FILTER (WHERE (r.access_level = 'free'::text)) AS free_resources,
    count(*) FILTER (WHERE (r.access_level = 'auth'::text)) AS auth_resources,
    count(*) FILTER (WHERE (r.access_level = 'basic'::text)) AS basic_resources,
    count(*) FILTER (WHERE (r.access_level = 'premium'::text)) AS premium_resources,
    count(*) FILTER (WHERE (r.access_level = 'professional'::text)) AS professional_resources,
    jsonb_agg(jsonb_build_object('id', r.id, 'title', r.title, 'subject_category', r.subject_category, 'level', r.level, 'access_level', r.access_level)) AS resources
   FROM (public.resources r
     JOIN public.resource_categories rc ON ((r.category_id = rc.id)))
  GROUP BY r.category_id, rc.name
  ORDER BY rc.name
  WITH NO DATA;


