    u.email,
    p.role,
    count(ua.id) AS total_achievements,
    count(ua.id) FILTER (WHERE (ua.unlocked = true)) AS unlocked_achievements,
    COALESCE(sum(a.points), (0)::bigint) AS total_points,
    COALESCE(sum(a.points) FILTER (WHERE (ua.unlocked = true)), (0)::bigint) AS unlocked_points,
    round((((count(ua.id) FILTER (WHERE (ua.unlocked = true)))::numeric / (NULLIF(count(ua.id), 0))::numeric) * (100)::numeric), 2) AS completion_percentage
   FROM (((auth.users u
     LEFT JOIN public.profiles p ON ((u.id = p.id)))
     LEFT JOIN public.user_achievements ua ON ((u.id = ua.user_id)))
     LEFT JOIN public.achievements a ON ((ua.achievement_id = a.id)))
  GROUP BY u.id, u.email, p.role;


