-- Name: user_payment_methods_formatted; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.user_payment_methods_formatted WITH (security_invoker='true') AS
 SELECT pm.id,
    pm.user_id,
    pm.type,
    pm.provider_id,
    pm.is_default,
    pm.created_at,
    pm.updated_at,
        CASE
            WHEN (pm.type = 'card'::public.payment_method_type) THEN jsonb_build_object('brand', (pm.details ->> 'brand'::text), 'last4', (pm.details ->> 'last4'::text), 'exp_month', (pm.details ->> 'exp_month'::text), 'exp_year', (pm.details ->> 'exp_year'::text), 'name', (pm.details ->> 'name'::text))
            WHEN (pm.type = 'mpesa'::public.payment_method_type) THEN jsonb_build_object('phone', (pm.details ->> 'phone'::text), 'name', 'M-Pesa')
            WHEN (pm.type = 'paypal'::public.payment_method_type) THEN jsonb_build_object('email', (pm.details ->> 'email'::text), 'name', 'PayPal')
            ELSE pm.details
        END AS formatted_details
   FROM public.payment_methods pm;


