--

CREATE VIEW public.user_roles_comprehensive AS
 SELECT p.id AS user_id,
    p.email,
    p.display_name,
    p.role AS profile_role,
    array_agg(ur.role) AS app_roles,
    bool_or((ur.role = 'admin'::public.app_role)) AS is_admin,
    bool_or((ur.role = 'tutor'::public.app_role)) AS is_tutor,
    bool_or((ur.role = 'user'::public.app_role)) AS is_user,
    p.created_at,
    p.updated_at
   FROM (public.profiles p
     LEFT JOIN public.user_roles ur ON ((p.id = ur.user_id)))
  GROUP BY p.id, p.email, p.display_name, p.role, p.created_at, p.updated_at;


