20250802021910	{"-- Add alternate_audio_path column to tracks table\nALTER TABLE public.tracks\nADD COLUMN alternate_audio_path text;\n\n-- Update Pale Ulipo track with alternate path\nUPDATE public.tracks\nSET alternate_audio_path = 'Tracks/Cover_Tracks/Pale Ulipo cover.m4a'\nWHERE title = 'Pale Ulipo';"}		saemstunes@gmail.com	\N
20250902030833	{"-- Create user roles enum\nCREATE TYPE public.app_role AS ENUM ('admin', 'tutor', 'user');\n\n-- Create user_roles table for proper role management\nCREATE TABLE public.user_roles (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,\n    role public.app_role NOT NULL,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,\n    UNIQUE (user_id, role)\n);\n\n-- Enable RLS on user_roles\nALTER TABLE public.user_roles ENABLE ROW LEVEL SECURITY;\n\n-- Create security definer function to check roles (prevents RLS recursion)\nCREATE OR REPLACE FUNCTION public.has_role(_user_id UUID, _role public.app_role)\nRETURNS BOOLEAN\nLANGUAGE SQL\nSTABLE\nSECURITY DEFINER\nSET search_path TO ''\nAS $$\n  SELECT EXISTS (\n    SELECT 1\n    FROM public.user_roles\n    WHERE user_id = _user_id AND role = _role\n  );\n$$;\n\n-- Create helper function to check if current user is admin\nCREATE OR REPLACE FUNCTION public.is_current_user_admin()\nRETURNS BOOLEAN\nLANGUAGE SQL\nSTABLE\nSECURITY DEFINER\nSET search_path TO ''\nAS $$\n  SELECT public.has_role(auth.uid(), 'admin'::public.app_role);\n$$;"}		saemstunes@gmail.com	\N
20250902030855	{"-- Add RLS policies for user_roles table\nCREATE POLICY \\"Users can view their own roles\\"\nON public.user_roles\nFOR SELECT\nUSING (auth.uid() = user_id);\n\nCREATE POLICY \\"Admins can manage all roles\\"\nON public.user_roles\nFOR ALL\nUSING (public.is_current_user_admin())\nWITH CHECK (public.is_current_user_admin());\n\n-- Add missing RLS policies for unprotected tables\n\n-- Fix access_tiers table\nALTER TABLE public.access_tiers ENABLE ROW LEVEL SECURITY;\n\nCREATE POLICY \\"Everyone can view access tiers\\"\nON public.access_tiers\nFOR SELECT\nUSING (true);\n\nCREATE POLICY \\"Only admins can manage access tiers\\"\nON public.access_tiers\nFOR ALL\nUSING (public.is_current_user_admin())\nWITH CHECK (public.is_current_user_admin());\n\n-- Fix media_files table\nALTER TABLE public.media_files ENABLE ROW LEVEL SECURITY;\n\nCREATE POLICY \\"Users can view media files\\"\nON public.media_files\nFOR SELECT\nUSING (true);\n\nCREATE POLICY \\"Authenticated users can upload media\\"\nON public.media_files\nFOR INSERT\nWITH CHECK (auth.uid() IS NOT NULL);\n\nCREATE POLICY \\"Only admins can manage all media files\\"\nON public.media_files\nFOR ALL\nUSING (public.is_current_user_admin())\nWITH CHECK (public.is_current_user_admin());\n\n-- Fix missing_artist_requests table\nALTER TABLE public.missing_artist_requests ENABLE ROW LEVEL SECURITY;\n\nCREATE POLICY \\"Only admins can view missing artist requests\\"\nON public.missing_artist_requests\nFOR SELECT\nUSING (public.is_current_user_admin());\n\nCREATE POLICY \\"Anyone can create missing artist requests\\"\nON public.missing_artist_requests\nFOR INSERT\nWITH CHECK (true);\n\n-- Update existing admin function to use new role system\nCREATE OR REPLACE FUNCTION public.is_admin(user_id UUID)\nRETURNS BOOLEAN\nLANGUAGE SQL\nSTABLE\nSECURITY DEFINER\nSET search_path TO ''\nAS $$\n  SELECT public.has_role(user_id, 'admin'::public.app_role);\n$$;\n\n-- Create trigger to automatically assign 'user' role to new signups\nCREATE OR REPLACE FUNCTION public.handle_new_user_role()\nRETURNS TRIGGER\nLANGUAGE plpgsql\nSECURITY DEFINER\nSET search_path TO ''\nAS $$\nBEGIN\n  INSERT INTO public.user_roles (user_id, role)\n  VALUES (NEW.id, 'user'::public.app_role);\n  RETURN NEW;\nEND;\n$$;\n\nCREATE TRIGGER on_auth_user_created_role\n  AFTER INSERT ON auth.users\n  FOR EACH ROW EXECUTE FUNCTION public.handle_new_user_role();"}		saemstunes@gmail.com	\N
20250911042331	{"-- Fix 1: Add missing approved column to video_content table\nALTER TABLE public.video_content \nADD COLUMN IF NOT EXISTS approved boolean DEFAULT true;\n\n-- Fix 2: Update video_content records to have approved = true\nUPDATE public.video_content \nSET approved = true \nWHERE approved IS NULL;\n\n-- Fix 3: Ensure proper user creation flow with consolidated trigger\n-- Drop existing conflicting trigger if it exists\nDROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;\n\n-- Create or replace the unified user creation function\nCREATE OR REPLACE FUNCTION public.handle_new_user()\nRETURNS trigger\nLANGUAGE plpgsql\nSECURITY DEFINER\nSET search_path = public\nAS $$\nBEGIN\n  -- Insert into profiles table\n  INSERT INTO public.profiles (id, email, role, subscription_tier)\n  VALUES (\n    NEW.id, \n    NEW.email,\n    COALESCE(NEW.raw_user_meta_data ->> 'role', 'user'),\n    'free'\n  )\n  ON CONFLICT (id) DO UPDATE SET\n    email = EXCLUDED.email,\n    role = EXCLUDED.role;\n\n  -- Insert into user_roles table with proper enum mapping\n  INSERT INTO public.user_roles (user_id, role)\n  VALUES (\n    NEW.id,\n    CASE \n      WHEN (NEW.raw_user_meta_data ->> 'role') = 'tutor' THEN 'tutor'::app_role\n      WHEN (NEW.raw_user_meta_data ->> 'role') = 'admin' THEN 'admin'::app_role\n      ELSE 'user'::app_role\n    END\n  )\n  ON CONFLICT (user_id, role) DO NOTHING;\n\n  RETURN NEW;\nEND;\n$$;\n\n-- Create the trigger for new user creation\nCREATE TRIGGER on_auth_user_created\n  AFTER INSERT ON auth.users\n  FOR EACH ROW EXECUTE FUNCTION public.handle_new_user();\n\n-- Fix 4: Ensure proper RLS policies for profiles table\n-- Drop existing conflicting policies and recreate them properly\nDROP POLICY IF EXISTS \\"Allow insert for authenticated users on profiles\\" ON public.profiles;\nDROP POLICY IF EXISTS \\"profiles_insert_own\\" ON public.profiles;\n\n-- Create a single, clear insert policy for profiles\nCREATE POLICY \\"Users can insert their own pr