20250913052313	{"-- Unified Learning Hub Database Schema\n\n-- Course categories table\nCREATE TABLE IF NOT EXISTS course_categories (\n  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n  title TEXT NOT NULL,\n  description TEXT,\n  icon TEXT,\n  color TEXT,\n  estimated_time TEXT,\n  order_index INTEGER DEFAULT 0,\n  prerequisites UUID[] DEFAULT '{}',\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now()\n);\n\n-- Courses table\nCREATE TABLE IF NOT EXISTS courses (\n  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n  title TEXT NOT NULL,\n  description TEXT,\n  category_id UUID REFERENCES course_categories(id),\n  instructor_id UUID REFERENCES profiles(id),\n  level TEXT CHECK (level IN ('beginner', 'intermediate', 'advanced')),\n  access_level TEXT CHECK (access_level IN ('free', 'auth', 'basic', 'premium', 'professional')),\n  lessons INTEGER DEFAULT 0,\n  duration INTEGER DEFAULT 0, -- in minutes\n  preview_type TEXT CHECK (preview_type IN ('video', 'audio', 'text', 'interactive')),\n  preview_url TEXT,\n  preview_duration INTEGER DEFAULT 60,\n  skills_count INTEGER DEFAULT 0,\n  enrollment_count INTEGER DEFAULT 0,\n  average_rating NUMERIC(3,2) DEFAULT 0.0,\n  estimated_time TEXT,\n  related_courses UUID[] DEFAULT '{}',\n  related_resources UUID[] DEFAULT '{}',\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now()\n);\n\n-- Course modules table\nCREATE TABLE IF NOT EXISTS course_modules (\n  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n  course_id UUID REFERENCES courses(id) ON DELETE CASCADE,\n  title TEXT NOT NULL,\n  description TEXT,\n  order_index INTEGER DEFAULT 0,\n  estimated_duration INTEGER DEFAULT 0, -- in minutes\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now()\n);\n\n-- Course lessons table  \nCREATE TABLE IF NOT EXISTS course_lessons (\n  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n  module_id UUID REFERENCES course_modules(id) ON DELETE CASCADE,\n  title TEXT NOT NULL,\n  description TEXT,\n  content_type TEXT CHECK (content_type IN ('video', 'audio', 'text', 'interactive', 'quiz')),\n  content_url TEXT,\n  order_index INTEGER DEFAULT 0,\n  duration INTEGER DEFAULT 0, -- in minutes\n  is_free_preview BOOLEAN DEFAULT false,\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now()\n);\n\n-- User course progress tracking\nCREATE TABLE IF NOT EXISTS user_course_progress (\n  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,\n  course_id UUID REFERENCES courses(id) ON DELETE CASCADE,\n  progress INTEGER DEFAULT 0 CHECK (progress >= 0 AND progress <= 100),\n  last_accessed TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  completed BOOLEAN DEFAULT false,\n  completed_at TIMESTAMP WITH TIME ZONE,\n  UNIQUE(user_id, course_id)\n);\n\n-- User lesson progress tracking\nCREATE TABLE IF NOT EXISTS user_lesson_progress (\n  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,\n  lesson_id UUID REFERENCES course_lessons(id) ON DELETE CASCADE,\n  completed BOOLEAN DEFAULT false,\n  watch_time INTEGER DEFAULT 0, -- seconds watched\n  last_position INTEGER DEFAULT 0, -- seconds\n  completed_at TIMESTAMP WITH TIME ZONE,\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  UNIQUE(user_id, lesson_id)\n);\n\n-- Update resources table to include course relationships\nALTER TABLE resources \nADD COLUMN IF NOT EXISTS related_courses UUID[] DEFAULT '{}';\n\n-- Course instructors table (optional - can use profiles)\nCREATE TABLE IF NOT EXISTS instructors (\n  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n  user_id UUID REFERENCES auth.users(id),\n  name TEXT NOT NULL,\n  bio TEXT,\n  expertise TEXT[],\n  profile_image_url TEXT,\n  social_links JSONB DEFAULT '{}',\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),\n  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now()\n);\n\n-- Enable RLS on all tables\nALTER TABLE course_categories ENABLE ROW LEVEL SECURITY;\nALTER TABLE courses ENABLE ROW LEVEL SECURITY;\nALTER TABLE course_modules ENABLE ROW LEVEL SECURITY;\nALTER TABLE course_lessons ENABLE ROW LEVEL SECURITY;\nALTER TABLE user_course_progress ENABLE ROW LEVEL SECURITY;\nALTER TABLE user_lesson_progress ENABLE ROW LEVEL SECURITY;\nALTER TABLE instructors ENABLE ROW LEVEL SECURITY;\n\n-- RLS Policies for course_categories\nCREATE POLICY \\"Anyone can view course categories\\" ON course_categories FOR SELECT USING (true);\nCREATE POLICY \\"Only admins can manage course categories\\" ON course_categories FOR ALL USING (is_admin()) WITH CHECK (is_admin());\n\n-- RLS Policies for courses\nCREATE POLICY \\"Anyone can view courses\\" ON courses FOR SELECT USING (true);\nCREATE POLICY \\"Only admins and instructors can manage courses\\" ON courses FOR ALL USING (is_admin() OR auth.uid() = instructor_id) WITH CHECK (is_admin() OR auth.uid() = instructor_id);\n\n-- RLS Policies for course_modules\nCREATE POLICY \\"Anyone can view course modules\\" ON course_modules FOR SELECT USING (true);\nCREATE POLICY \\"Only admins and course instructors can manage modules\\" ON course_modules FOR ALL USING (\n  is_admin() OR EXISTS (SELECT 1 FROM courses WHERE courses.id = course_modules.course_id AND courses.instructor_id = auth.uid())\n) WITH CHECK (\n  is_admin() OR EXISTS (SELECT 1 FROM courses WHERE courses.id = course_modules.course_id AND courses.instructor_id = auth.uid())\n);\n\n-- RLS Policies for course_lessons  \nCREATE POLICY \\"Anyone can view course lessons\\" ON course_lessons FOR SELECT USING (true);\nCREATE POLICY \\"Only admins and course instructors can manage lessons\\" ON course_lessons FOR ALL USING (\n  is_admin() OR EXISTS (\n    SELECT 1 FROM course_modules cm \n    JOIN courses c ON c.id = cm.course_id \n    WHERE cm.id = course_lessons.module_id AND c.instructor_id = auth.uid()\n  )\n) WITH CHECK (\n  is_admin() OR EXISTS (\n    SELECT 1 FROM course_modules cm \n    JOIN courses c ON c.id = cm.course_id \n    WHERE cm.id = course_lessons.module_id AND c.instructor_id = auth.uid()\n  )\n);\n\n-- RLS Policies for user_course_progress\nCREATE POLICY \\"Users can view their own course progress\\" ON user_course_progress FOR SELECT USING (auth.uid() = user_id);\nCREATE POLICY \\"Users can manage their own course progress\\" ON user_course_progress FOR ALL USING (auth.uid() = user_id) WITH CHECK (auth.uid() = user_id);\n\n-- RLS Policies for user_lesson_progress\nCREATE POLICY \\"Users can view their own lesson progress\\" ON user_lesson_progress FOR SELECT USING (auth.uid() = user_id);\nCREATE POLICY \\"Users can manage their own lesson progress\\" ON user_lesson_progress FOR ALL USING (auth.uid() = user_id) WITH CHECK (auth.uid() = user_id);\n\n-- RLS Policies for instructors\nCREATE POLICY \\"Anyone can view instructors\\" ON instructors FOR SELECT USING (true);\nCREATE POLICY \\"Users can manage their own instructor profile\\" ON instructors FOR ALL USING (auth.uid() = user_id) WITH CHECK (auth.uid() = user_id);\nCREATE POLICY \\"Admins can manage all instructor profiles\\" ON instructors FOR ALL USING (is_admin()) WITH CHECK (is_admin());\n\n-- Create indexes for better performance\nCREATE INDEX IF NOT EXISTS idx_courses_category_id ON courses(category_id);\nCREATE INDEX IF NOT EXISTS idx_courses_instructor_id ON courses(instructor_id);\nCREATE INDEX IF NOT EXISTS idx_courses_access_level ON courses(access_level);\nCREATE INDEX IF NOT EXISTS idx_course_modules_course_id ON course_modules(course_id);\nCREATE INDEX IF NOT EXISTS idx_course_lessons_module_id ON course_lessons(module_id);\nCREATE INDEX IF NOT EXISTS idx_user_course_progress_user_id ON user_course_progress(user_id);\nCREATE INDEX IF NOT EXISTS idx_user_course_progress_course_id ON user_course_progress(course_id);\nCREATE INDEX IF NOT EXISTS idx_user_lesson_progress_user_id ON user_lesson_progress(user_id);\nCREATE INDEX IF NOT EXISTS idx_user_lesson_progress_lesson_id ON user_lesson_progress(lesson_id);\n\n-- Insert sample course categories\nINSERT INTO course_categories (title, description, icon, color, order_index) VALUES\n('Guitar Fundamentals', 'Learn the basics of guitar playing', 'Guitar', 'bg-amber-500', 1),\n('Piano Mastery', 'Complete piano course from beginner to advanced', 'Piano', 'bg-blue-500', 2),\n('Music Theory', 'Understanding the theory behind music', 'BookOpen', 'bg-purple-500', 3),\n('Vocal Training', 'Develop your singing voice', 'Mic', 'bg-pink-500', 4),\n('Gospel Music', 'Learn gospel music traditions and techniques', 'Heart', 'bg-gold', 5),\n('Music Production', 'Create and produce your own music', 'Headphones', 'bg-green-500', 6)\nON CONFLICT DO NOTHING;\n\n-- Insert sample courses\nINSERT INTO courses (title, description, category_id, level, access_level, lessons, duration, preview_type, preview_url, enrollment_count) \nSELECT \n  'Beginner Guitar Course',\n  'Learn guitar from scratch with step-by-step lessons',\n  cc.id,\n  'beginner',\n  'free',\n  12,\n  480,\n  'video',\n  '/previews/guitar-intro.mp4',\n  245\nFROM course_categories cc WHERE cc.title = 'Guitar Fundamentals'\nON CONFLICT DO NOTHING;\n\nINSERT INTO courses (title, description, category_id, level, access_level, lessons, duration, preview_type, preview_url, enrollment_count)\nSELECT\n  'Advanced Piano Techniques',\n  'Master advanced piano playing techniques and theory',\n  cc.id,\n  'advanced',\n  'premium',\n  24,\n  960,\n  'video',\n  '/previews/piano-advanced.mp4',\n  89\nFROM course_categories cc WHERE cc.title = 'Piano Mastery'\nON CONFLICT DO NOTHING;\n\nINSERT INTO courses (title, description, category_id, level, access_level, lessons, duration, preview_type, preview_url, enrollment_count)\nSELECT\n  'Gospel Music Essentials',\n  'Learn the fundamentals of gospel music performance',\n  cc.id,\n  'intermediate',\n  'basic',\n  18,\n  720,\n  'video',\n  '/previews/gospel-essentials.mp4',\n  156\nFROM course_categories cc WHERE cc.title = 'Gospel Music'\nON CONFLICT DO NOTHING;\n\n-- Create function to update course enrollment count\nCREATE OR REPLACE FUNCTION update_course_enrollment_count()\nRETURNS TRIGGER AS $$\nBEGIN\n  UPDATE courses \n  SET enrollment_count = (\n    SELECT COUNT(*) \n    FROM user_course_progress \n    WHERE course_id = NEW.course_id\n  )\n  WHERE id = NEW.course_id;\n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Create trigger to automatically update enrollment count\nCREATE TRIGGER update_course_enrollment_trigger\n  AFTER INSERT ON user_course_progress\n  FOR EACH ROW\n  EXECUTE FUNCTION update_course_enrollment_count();\n\n-- Create function to calculate course progress\nCREATE OR REPLACE FUNCTION calculate_course_progress(course_id_param UUID, user_id_param UUID)\nRETURNS INTEGER AS $$\nDECLARE\n  total_lessons INTEGER;\n  completed_lessons INTEGER;\n  progress_percent INTEGER;\nBEGIN\n  -- Get total lessons in course\n  SELECT COUNT(cl.id) INTO total_lessons\n  FROM course_lessons cl\n  JOIN course_modules cm ON cm.id = cl.module_id\n  WHERE cm.course_id = course_id_param;\n  \n  -- Get completed lessons by user\n  SELECT COUNT(ulp.id) INTO completed_lessons\n  FROM user_lesson_progress ulp\n  JOIN course_lessons cl ON cl.id = ulp.lesson_id\n  JOIN course_modules cm ON cm.id = cl.module_id\n  WHERE cm.course_id = course_id_param \n    AND ulp.user_id = user_id_param \n    AND ulp.completed = true;\n  \n  -- Calculate percentage\n  IF total_lessons > 0 THEN\n    progress_percent := ROUND((completed_lessons::NUMERIC / total_lessons::NUMERIC) * 100);\n  ELSE\n    progress_percent := 0;\n  END IF;\n  \n  -- Update user course progress\n  INSERT INTO user_course_progress (user_id, course_id, progress, last_accessed)\n  VALUES (user_id_param, course_id_param, progress_percent, now())\n  ON CONFLICT (user_id, course_id) \n  DO UPDATE SET \n    progress = EXCLUDED.progress,\n    last_accessed = EXCLUDED.last_accessed,\n    completed = (EXCLUDED.progress = 100),\n    completed_at = CASE WHEN EXCLUDED.progress = 100 AND user_course_progress.completed = false THEN now() ELSE user_course_progress.completed_at END;\n  \n  RETURN progress_percent;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Create trigger to update course progress when lesson is completed\nCREATE OR REPLACE FUNCTION update_course_progress_on_lesson_complete()\nRETURNS TRIGGER AS $$\nDECLARE\n  course_id_var UUID;\nBEGIN\n  -- Get course ID from lesson\n  SELECT cm.course_id INTO course_id_var\n  FROM course_lessons cl\n  JOIN course_modules cm ON cm.id = cl.module_id\n  WHERE cl.id = NEW.lesson_id;\n  \n  -- Update course progress\n  PERFORM calculate_course_progress(course_id_var, NEW.user_id);\n  \n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER update_course_progress_trigger\n  AFTER INSERT OR UPDATE ON user_lesson_progress\n  FOR EACH ROW\n  WHEN (NEW.completed = true)\n  EXECUTE FUNCTION update_course_progress_on_lesson_complete();"}		saemstunes@gmail.com	\N
